class Solution:
    # @param {string} s
    # @param {string[]} words
    # @return {integer[]}
    def findSubstring(self, s, words):
        inis = {}
        ws_c = {}
        for w in words:
            if(w[0] in inis): inis[w[0]].append(w)
            else: inis[w[0]] = [w]
            if(w in ws_c): ws_c[w] += 1
            else: ws_c[w] = 1
        res = []
        qs = {}
        # p q start at the same point 
        # if found a word remove it from ws
        # then p stay while q jump to next word
        #   record q postion
        #   if found all reset ws and add p to res
        #   p move to next word and q back to p
        # if not found p move to q next q move to next

        # find all words record its position to qs
        q = 0

        while(q < len(s)):
            if(s[q] in inis):
                for w in inis[s[q]]:
                    if(s[q:q + len(w)] == w): 
                        if(q not in qs): qs[q] = w
                        break
            q += 1
        # print qs
        ws = dict(ws_c)
        p, q, i = 0, 0, 0
        while(p < len(s) and q < len(s)):
            # print p, q
            if(q not in qs): 
                p += 1
                while(p < len(s) and p not in qs): p += 1
                q = p
                ws = dict(ws_c)
                continue
            w = qs[q]
            if(w in ws): 
                if(ws[w] == 1): ws.pop(w)
                else:
                    ws[w] -= 1
                if(not ws):
                    ws = dict(ws_c)
                    res.append(p)
                    p += 1
                    while(p < len(s) and p not in qs): p += 1
                    q = p
                else:
                    q += len(w)
            else:
                p += 1
                while(p < len(s) and p not in qs): p += 1
                q = p
                ws = dict(ws_c)
        return res
