    def isMatch(self, s, p):
        if(len(p) == 0): return p == s
        i, j = 0, 0
        star_i, star_j = -1, -1
        while(i < len(s)):
            if(j < len(p) and (s[i] == p[j] or p[j] == '?')): i, j = i + 1, j + 1
            elif(j < len(p) and p[j] == '*'): 
                star_i, star_j = i + 1, j
                j += 1
            elif(star_i >= 0):
                i, j = star_i, star_j
            else:
                return False
        for each in p[j:]:
            if(not each == '*'): return False
        return True
